import React, { useState } from 'react';

function TwoApp(props) {

    //객체처리변수
    //변수를 객체처리로 이렇게 담을 수도 있다
    const [inputs,setInputs] =useState({
        name:'강호동',
        addr:'서울시 강남구',
        age:20
    });

    //3개의 공통함수
    const changeData=(e)=>{
        console.log("name: "+e.target.value);
        console.log("value: "+e.target.value);

        //name.value값 얻기
        //e.target이 가지고 있는 객체 한번에 변경하기
        const {name,value}=e.target;
    }


    return (
        <div>
            <h3 className='alert alert-warning'>TwoApp입니다_state 를 객체변수로</h3>
                                                    {/* 위에 쓴 useState에서 변수로 담은 값을 이렇게 가져올 수 있다 */}
            <h3>이름: <input type='text' name='name' defaultValue={inputs.name}
            onChange={changeData}/></h3>
            <h3>주소: <input type='text' name='name' defaultValue={inputs.addr}
            onChange={changeData}/></h3>
            <h3>나이: <input type='text' name='name' defaultValue={inputs.age}
            onChange={changeData}/></h3>

            <hr/>
            <h2>이름: </h2>
        </div>
    );
}

export default TwoApp;<h3 className='alert alert-warning'>OneApp입니다</h3>
