import React, { useState } from 'react';
import Alert from '@mui/material/Alert';
import AddToQueueIcon from '@mui/icons-material/AddToQueue';
import BatterySaverIcon from '@mui/icons-material/BatterySaver';
import TwoSubApp from './TwoSubApp';
import TwoSubApp2 from './TwoSubApp2';

function TwoApp(props) {

    const [color,setColor]=useState("cadetblue");
    const [message,setMessage]=useState("오늘은 수요일*^^*");
    const [photo,setPhoto]=useState("05");

    //TwoSubApp2 변수
    const [likeColor,setLikeColor]=useState(['pink','yellow','gray']);


    //3개의 이벤트를 만들어 전달
    const changeMsg=(msg)=>{

        setMessage(msg);
    }

    const changeColor=(col)=>{

        setColor(col);
    }

    const changePhoto=(p)=>{
        setPhoto(p);
    }


    //TwoSubApp2...이벤트 색상추가
    const addColor=(co)=>{
        // (likeColor.concat(내가 선택한 색));
        setLikeColor(likeColor.concat(co));
    }

    const deleteCol=(index)=>{
        setLikeColor(likeColor.filter((item,idx)=>idx!==index));
    }

    return (
        <div>
            <Alert severity="warning">TwoApp<AddToQueueIcon/><BatterySaverIcon/></Alert>
            <br/>
            <h1 style={{color:color}}>{message}</h1>
            <img src={`../image2/${photo}.png`}/>
            <br/>
            {likeColor.map((co,idx)=>(<div className='box' style={{backgroundColor:co}} onDoubleClick={()=>deleteCol(idx)}>{idx+1}</div>))}
            <br/><br/><br/><br/>
            <TwoSubApp photo={changePhoto} onMessage={changeMsg} onColor={changeColor}/>
            <br/>
            <TwoSubApp2 onAddColor={addColor}/>
        </div>
    );
}

export default TwoApp;
